import React, { useState, useEffect } from 'react';
import ReactPlayer from 'react-player';

function RecordedFootageDetection() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [videoUrl, setVideoUrl] = useState('');
  const [anomalies, setAnomalies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPreview, setShowPreview] = useState(false);
  const [outputFile, setOutputFile] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [outputFilesList, setOutputFilesList] = useState([]);
  const [selectedOutputFile, setSelectedOutputFile] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setVideoUrl('');
    setAnomalies([]);
    setError('');
    setShowPreview(false);
    setOutputFile('');
    setTimestamp('');
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError('Please select a video file first.');
      return;
    }
    setLoading(true);
    setError('');
    setShowPreview(false);
    const formData = new FormData();
    formData.append('video', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/detect', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) {
        throw new Error('Failed to process video');
      }
      const data = await response.json();
      setAnomalies(data.anomalies || []);
      setVideoUrl(data.video_url);
      setOutputFile(data.output_file || '');
      setTimestamp(data.timestamp || '');
      setShowPreview(true);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchOutputFiles = async () => {
    try {
      const response = await fetch('http://localhost:8000/list_output_files');
      if (!response.ok) {
        throw new Error('Failed to fetch output files');
      }
      const data = await response.json();
      setOutputFilesList(data.files || []);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchOutputFiles();
  }, []);

  const handleSelectOutputFile = (filename) => {
    setSelectedOutputFile(filename);
  };

  return (
    <div>
      <h2>Recorded Footage Detection</h2>
      <input type="file" accept="video/*" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={loading}>
        {loading ? 'Processing...' : 'Upload and Detect'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <h3>Output Files</h3>
      <button onClick={fetchOutputFiles}>Refresh Output Files</button>
      <ul>
        {outputFilesList.map((file) => (
          <li key={file}>
            <button onClick={() => handleSelectOutputFile(file)}>{file}</button>
          </li>
        ))}
      </ul>

      {/* Removed video preview as per request */}
      {/* {selectedOutputFile && (
        <div>
          <h3>Preview: {selectedOutputFile}</h3>
          <ReactPlayer
            url={`http://localhost:8000/processed_video/${selectedOutputFile}`}
            controls
            width="600px"
            height="auto"
          />
        </div>
      )} */}

      {anomalies.length > 0 && (
        <div>
          <h3>Detected Anomalies</h3>
          <ul>
            {anomalies.map((anomaly) => (
              <li key={anomaly.id}>
                ID: {anomaly.id}, Class: {anomaly.class}, Description: {anomaly.description}
              </li>
            ))}
          </ul>
          <p>
            Output File: <a href={`http://localhost:8000/processed_video/${outputFile}`} target="_blank" rel="noopener noreferrer">{outputFile}</a><br />
            Saved at: {timestamp}
          </p>
        </div>
      )}
    </div>
  );
}

export default RecordedFootageDetection;
