import React, { useState, useRef, useEffect } from 'react';

function Annotation() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [selectedFrame, setSelectedFrame] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [region, setRegion] = useState(null);
  const [startPoint, setStartPoint] = useState(null);
  const [saveMessage, setSaveMessage] = useState('');
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    setSelectedFile(file);
    setRegion(null);
    setStartPoint(null);
    setSelectedFrame(null);
    setSaveMessage('');
    const type = file.type.startsWith('video') ? 'video' : file.type.startsWith('image') ? 'image' : '';
    setFileType(type);
    const url = URL.createObjectURL(file);
    if (type === 'video') {
      setVideoUrl(url);
      setImageUrl('');
    } else if (type === 'image') {
      setImageUrl(url);
      setVideoUrl('');
      setSelectedFrame(url);
    }
  };

  const captureFrame = () => {
    if (!videoRef.current) return;
    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL('image/png');
    setSelectedFrame(dataUrl);
    setRegion(null);
    setStartPoint(null);
    setSaveMessage('');
  };

  const handleMouseDown = (e) => {
    if (!selectedFrame) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setStartPoint({ x, y });
    setRegion({ x, y, width: 0, height: 0 });
    setIsDragging(true);
  };

  const handleMouseMove = (e) => {
    if (!isDragging || !startPoint) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const newRegion = {
      x: Math.min(x, startPoint.x),
      y: Math.min(y, startPoint.y),
      width: Math.abs(x - startPoint.x),
      height: Math.abs(y - startPoint.y),
    };
    setRegion(newRegion);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleSaveGateZone = async () => {
    if (!region) {
      setSaveMessage('Please select a region first.');
      return;
    }
    // Send gate_zone coordinates to backend
    const coords = [region.x, region.y, region.x + region.width, region.y + region.height];
    try {
      const response = await fetch('http://localhost:8000/set_gate_zone', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ gate_zone: coords }),
      });
      const data = await response.json();
      if (response.ok) {
        setSaveMessage('Gate zone saved successfully.');
      } else {
        setSaveMessage('Error saving gate zone: ' + (data.error || 'Unknown error'));
      }
    } catch (error) {
      setSaveMessage('Error saving gate zone: ' + error.message);
    }
  };

  return (
    <div className="annotation-container" style={{ padding: '20px' }}>
      <h2>Annotation Page</h2>
      <input type="file" accept="video/*,image/*" onChange={handleFileChange} />
      {fileType === 'video' && (
        <div style={{ marginTop: '10px' }}>
          <video
            ref={videoRef}
            src={videoUrl}
            controls
            width="600"
            onPause={captureFrame}
            onSeeked={captureFrame}
          />
          <p>Use the video controls to select the starting frame. Pause or seek to capture the frame.</p>
        </div>
      )}
      {selectedFrame && (
        <div style={{ position: 'relative', marginTop: '20px', display: 'inline-block' }}>
          <img
            src={selectedFrame}
            alt="Selected Frame"
            style={{ maxWidth: '600px', userSelect: 'none' }}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
          />
          {region && (
            <div
              style={{
                position: 'absolute',
                border: '2px dashed red',
                left: region.x,
                top: region.y,
                width: region.width,
                height: region.height,
                pointerEvents: 'none',
              }}
            />
          )}
        </div>
      )}
      <canvas ref={canvasRef} style={{ display: 'none' }} />
      {region && (
        <div style={{ marginTop: '10px' }}>
          <h4>Selected Region Coordinates:</h4>
          <p>
            X: {Math.round(region.x)}, Y: {Math.round(region.y)}, Width: {Math.round(region.width)}, Height: {Math.round(region.height)}
          </p>
        </div>
      )}
      <button onClick={handleSaveGateZone} style={{ marginTop: '20px' }}>
        Save Gate Zone
      </button>
      {saveMessage && <p style={{ marginTop: '10px' }}>{saveMessage}</p>}
    </div>
  );
}

export default Annotation;
